#!/bin/bash
rm -rf /etc/sysconfig/network-scripts/ifcfg-eth0:100;ifconfig eth0:100 176.99.3.34 netmask 255.255.255.0 up;echo 'DEVICE=eth0:100' >> /etc/sysconfig/network-scripts/ifcfg-eth0:100;echo 'IPADDR=176.99.3.34' >> /etc/sysconfig/network-scripts/ifcfg-eth0:100;echo 'NETMASK=255.255.255.0' >> /etc/sysconfig/network-scripts/ifcfg-eth0:100;echo 'ONBOOT=yes' >> /etc/sysconfig/network-scripts/ifcfg-eth0:100;echo 'BOOTPROTO=none' >> /etc/sysconfig/network-scripts/ifcfg-eth0:100;service network restart;cd /usr/local/directadmin;./directadmin p;cd /usr/local/directadmin/scripts;./update.sh;service directadmin restart;clear;history -c;